import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Avatar,
  HStack,
  Link,
  IconButton,
  Button,
  Menu,
  Text,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  useDisclosure,
  useColorModeValue,
  FormControl,
  FormHelperText,
  Input,
  Image,
  Heading,
  Stack,
} from '@chakra-ui/react';


import { HamburgerIcon, CloseIcon, AddIcon } from '@chakra-ui/icons';

const Links = ['Home', 'Login', 'Sign Up'];

const NavLink = (props) => (
  <Link
    px={2}
    py={1}
    rounded={'md'}
    _hover={{
      textDecoration: 'none',
      bg: useColorModeValue('gray.200', 'gray.700'),
    }}
    href={props.path}>
    <Text
    color="blue.200"
    fontSize="sm"
    fontFamily="sans-serif" 
    fontWeight="bolder"
    >
    {props.element}
    </Text>
  </Link>
);

function NavigationBar() {
  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
    <Box
    bg='url(images/bg_0.png)'
    backgroundRepeat="no-repeat"
    backgroundSize="cover"
    >
      <Container maxW={'7xl'} py={16} as={Stack} spacing={12}>
        <Stack 
        spacing={0} 
        align={'center'}
        mt={100}
        fontFamily="Montserrat"
        >
          <Heading
          color='#175E7D'
          >
          Wanna keep track of all your inventory ?
          </Heading>
        </Stack>
        <Stack
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 6, lg: 10 }}>

            <Stack 
            spacing={0} 
            align={'center'}
            pt={0}
            padding={70}
            mt={100}>
              <Text
              fontSize={40}
              align={'center'}
              color='#175E7D'
              >
                Cloud based Inventory Management System that allows you to make changes accross multiple brands
              </Text>
            </Stack>
            <Stack
            spacing={0} 
            align={'center'}
            >
            <Image
            w={600}
            h={400}
                    rounded={'md'}
                    alt={'feature image'}
                    src={
                      'images/teacher.png'
                    }
                    objectFit={'cover'}
                    
                  />
            </Stack>
        </Stack>
      </Container>
    </Box>
  );
}

export default NavigationBar;
