import {
    Container,
    SimpleGrid,
    Divider,
    List,
    ListIcon,
    ListItem,
    Image,
    Flex,
    Heading,
    Text,
    Stack,
    StackDivider,
    Box,
    Icon,
    Avatar,
    Button,
    useColorModeValue,
  } from '@chakra-ui/react';
  import {
    IoAnalyticsSharp,
    IoLogoBitcoin,
    IoSearchSharp,
  } from 'react-icons/io5';
  import { ReactElement } from 'react';
  import { FaCheckCircle } from 'react-icons/fa';
  
  export default function Description_1() {
    return (
      <Container
      maxH={'2xl'} 
      maxW={'10xl'}
      bg='url(images/bg_1.png)'
      backgroundRepeat="no-repeat"
      backgroundSize="cover"
      alignItems={'center'}
      justifyContent='space-between'
      direction={'row'}
      display="flex"
      paddingRight={'20%'}
      paddingLeft={'20%'}
      >
        <Stack 
        flex={1}
        mt={20}
        >
          <Image
            h={300}
            w={'50%'}
            rounded={'md'}
            alt={'feature image'}
            src={
              'images/boxes_man.png'
            }
            objectFit={'fill'}
          />
        </Stack>
        <Stack
        flex={1}
        padding={30}
        bg="rgba(0,0,0,0.3)"
        color="white"
        fontFamily='Montserrat'
        w={550}
        h={'100%'}
        mb={-10}
        >
            <Heading
            fontWeight={500}
            fontSize={40}
            mb={30}
            >
              Helps you manage your inventory business easily
            </Heading>
            <Text
            fontSize={25}
            fontWeight={600}
            >
            Run efficient business with the best inventory management system
            </Text>
        </Stack>
      </Container>
    );
  }
