import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Avatar,
  HStack,
  Link,
  IconButton,
  Button,
  Menu,
  Text,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  useDisclosure,
  useColorModeValue,
  FormControl,
  FormHelperText,
  Input,
  Image,
  Heading,
  Container,
  Stack,
  FormLabel,
} from '@chakra-ui/react';


import { HamburgerIcon, CloseIcon, AddIcon } from '@chakra-ui/icons';

const Links = ['Home', 'Login', 'Sign Up'];

const NavLink = (props) => (
  <Link
    px={2}
    py={1}
    rounded={'md'}
    _hover={{
      textDecoration: 'none',
      bg: useColorModeValue('gray.200', 'gray.700'),
    }}
    href={props.path}>
    <Text
    color={props.color}
    fontSize={props.fsize}
    fontFamily="Montserrat" 
    fontWeight={props.fweight}
    bg={props.bground}
    padding={props.pad}
    >
    {props.element}
    </Text>
  </Link>
);

function NavigationBar() {
  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
  
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
    <Container
      maxW={'7xl'}
      py={1}
      as={Box}
      paddingRight={'1%'}
      paddingLeft={'1%'}
      spacing={0}
      bg='url(images/bg_0.png)'
      backgroundRepeat="no-repeat"
      backgroundSize="cover"
      sx={{ minWidth: '100vw', display: 'flex', justifyContent: 'center' }}
    >
    <Stack
          direction="row"
          alignItems="center"
          width="100%"
          justifyContent="space-between"
        >
        <Stack>
        <Image
            w={200}
            h={85}
            rounded={'md'}
            alt={'feature image'}
            src={
              'images/logo.png'
            }
            objectFit={'fill'}
            />
        </Stack>
        <Stack alignItems={'center'} justifyContent="space-between" direction="row">
          <NavLink color='#27ABE5' pad={1} fweight='bolder' fsize='xl' path="/" element="Home">Home</NavLink>
          <NavLink color='#27ABE5' pad={1} fweight="bold" fsize='xl' path="/features" element="Features">Features</NavLink>
          <NavLink color='#27ABE5' pad={1} fweight="bold" fsize='xl' path="/pricing" element="Pricing">Pricing</NavLink>
        </Stack>
        <Stack
        spacing={3}
        >
        <FormControl display="flex" direction="row">
            <Stack
            flex={1}
            >
              <Text
              color={'blue.200'}
              >
                Search
              </Text>
            </Stack>
            <Stack>
            <Input
            flex={1}
            type="text"
            name="search"
            w={500}
            >
            </Input>
            </Stack>
          </FormControl>
        </Stack>

        <Stack alignItems={'center'} justifyContent="space-between" direction="row">
        <NavLink path="/login" pad={1} fweight="bold" fsize='md' element="SIGN UP  |"/>
        <NavLink path="/signup" pad={2} fsize='md' bground="#27ABE5" color="white" element="SIGN IN NOW"/>
        </Stack>
          
      </Stack>
</Container>
  </div>
  );
}


export default NavigationBar;
