import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Heading,
  Text,
  Stack,
  Container,
  Avatar,
  useColorModeValue,
  Image
} from '@chakra-ui/react';



export default function Description_2() {
  return (
    <Box bg={useColorModeValue('gray.100', 'gray.700')}>
      <Container maxW={'7xl'} py={16} as={Stack} spacing={12}>
        <Stack spacing={0} align={'center'}>
        <Heading>Wanna keep track of all your inventory ?</Heading>
        </Stack>
        <Stack
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 4, lg: 10 }}>

            <Stack spacing={0} align={'center'}>
              <Heading>Wanna keep track of all your inventory ?</Heading>
            </Stack>
            <Stack
            spacing={0} align={'center'}
            >
            <Image
                    rounded={'md'}
                    alt={'feature image'}
                    src={
                      'images/teacher.png'
                    }
                    objectFit={'cover'}
                    
                  />
            </Stack>
        </Stack>
      </Container>
    </Box>
  );
}
