import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Heading,
  Text,
  Stack,
  Container,
  Avatar,
  useColorModeValue,
  Image
} from '@chakra-ui/react';



export default function Description_2() {
  return (
    <Box
    bg='url(images/bg_0.png)'
    backgroundRepeat="no-repeat"
    backgroundSize="cover"
    >
      <Container maxW={'80%'} py={16} as={Stack} spacing={12}>
        <Stack 
        spacing={0} 
        align={'center'}
        mt={100}
        fontFamily="Montserrat"
        >
          <Heading
          color='#175E7D'
          >
          Wanna keep track of all your inventory ?
          </Heading>
        </Stack>
        <Stack
          display="flex"
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 6, lg: 10 }}
          justifyContent="space-between"
          >

            <Stack 
            spacing={0} 
            align={'center'}
            pt={0}
            padding={70}
            flex={1.5}
            mt={40}>
              <Text
              fontSize={40}
              align={'center'}
              color='#175E7D'
              >
                Cloud based Inventory Management System that allows you to make changes accross multiple brands
              </Text>
            </Stack>
            <Stack
            flex={1}
            spacing={0} 
            align={'center'}
            padding={20}
            >
            <Image
            w={600}
            h={400}
            rounded={'md'}
            alt={'feature image'}
            src={
            'images/teacher.png'
            }
            objectFit={'fill'}
            />
            </Stack>
        </Stack>
      </Container>
    </Box>
  );
}
