import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Avatar,
  HStack,
  Link,
  IconButton,
  Button,
  Menu,
  Text,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  useDisclosure,
  useColorModeValue,
  FormControl,
  FormHelperText,
  Input,
  Image,
  Heading,
  Container,
  Stack,
  FormLabel,
} from '@chakra-ui/react';


import { HamburgerIcon, CloseIcon, AddIcon } from '@chakra-ui/icons';

const Links = ['Home', 'Login', 'Sign Up'];

const NavLink = (props) => (
  <Link
    px={2}
    py={1}
    rounded={'md'}
    _hover={{
      textDecoration: 'none',
      bg: useColorModeValue('gray.200', 'gray.700'),
    }}
    href={props.path}>
    <Text
    color="blue.200"
    fontSize="sm"
    fontFamily="sans-serif" 
    fontWeight="bolder"
    >
    {props.element}
    </Text>
  </Link>
);

function NavigationBar() {
  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
  
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
    <Container
      maxW={'7xl'}
      py={1}
      as={Box}
      padding={0}
      spacing={0}
      bg='url(images/bg_0.png)'
      backgroundRepeat="no-repeat"
      backgroundSize="cover"
      sx={{ minWidth: '100vw', display: 'flex', justifyContent: 'center' }}
    >
    <Stack
          direction="row"
          alignItems="center"
          width="100%"
          justifyContent="space-between"
        >
         <Image
         src='url(images/logo.png)'
         >
         </Image>
          <NavLink path="/" element="Home">Home</NavLink>
          <NavLink path="/features" element="Features">Features</NavLink>
          <NavLink path="/pricing" element="Pricing">Pricing</NavLink>
          <FormControl display="flex" direction="row">
            <Stack
            flex={1}
            >
              <Text>
                Search
              </Text>
            </Stack>
            <Stack>
            <Input
            flex={2}
            type="text"
            name="search"
            >
            </Input>
            </Stack>
          </FormControl>
        </Stack>
</Container>
  </div>
  );
}


export default NavigationBar;
