import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Heading,
  Text,
  Stack,
  Container,
  Avatar,
  useColorModeValue,
  Image
} from '@chakra-ui/react';



export default function Description_5() {
  return (
    <Box
    bg='url(images/bg_0.png)'
    backgroundRepeat="no-repeat"
    backgroundSize="cover"
    >
      <Container maxW={'80%'} py={16} as={Stack} spacing={0}>
        <Stack
          display="flex"
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 6, lg: 10 }}
          justifyContent="space-between"
          >

            <Stack 
            flex={2}
            spacing={0} 
            align={'center'}
            pt={0}
            padding={0}
            mt={50}>
                <Heading
                color='#175E7D'
                >
                  Flexible
                </Heading>
                  <Text
                  fontSize={30}
                  align={'center'}
                  color='#175E7D'
                  px={6}
                  >
                  Clearly inventory can be used for a wide variety of situations including normal warehouse operations, 
                  art collections, pawn shops, manufacturers, charitible organizations traking donations, tooling inventory,
                  and so much more.
                  </Text>
            </Stack>
            <Stack
            flex={1}
            spacing={0} 
            align={'center'}
            >
                <Image
                w={'100%'}
                h={800}
                rounded={'md'}
                alt={'feature image'}
                src={
                  'images/computer.png'
                }
                objectFit={'fill'}
                />
            </Stack>
        </Stack>
      </Container>
    </Box>
  );
}
