import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Avatar,
  HStack,
  Link,
  IconButton,
  Button,
  Menu,
  Text,
  MenuButton,
  MenuList,
  MenuItem,
  MenuDivider,
  useDisclosure,
  useColorModeValue,
  FormControl,
  FormHelperText,
  Input,
  Image,
  Heading,
  Container,
  Stack,
} from '@chakra-ui/react';


import { HamburgerIcon, CloseIcon, AddIcon } from '@chakra-ui/icons';

const Links = ['Home', 'Login', 'Sign Up'];

const NavLink = (props) => (
  <Link
    px={2}
    py={1}
    rounded={'md'}
    _hover={{
      textDecoration: 'none',
      bg: useColorModeValue('gray.200', 'gray.700'),
    }}
    href={props.path}>
    <Text
    color="blue.200"
    fontSize="sm"
    fontFamily="sans-serif" 
    fontWeight="bolder"
    >
    {props.element}
    </Text>
  </Link>
);

function NavigationBar() {
  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
  
    <Container 
    //maxW={'7xl'} 
    maxW={'100%'} 
    py={1} 
    as={Box} 
    spacing={12}
    bg='url(images/bg_0.png)'
    backgroundRepeat="no-repeat"
    backgroundSize="cover"
    >
      <Flex h={16} width="100%" justifyContent={'space-between'} alignItems="center" flexWrap="wrap">
          <IconButton
            size={'md'}
            icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
            aria-label={'Open Menu'}
            display={{ md: 'none' }}
            onClick={isOpen ? onClose : onOpen}
          />
          <HStack spacing={8} alignItems={'center'}>
            <Box> <Avatar h={45} w={75} src="images/logo.png"/></Box>
            <HStack
              as={'nav'}
              spacing={4}
              display={{ base: 'none', md: 'flex' }}>
              <NavLink element='Home' path='/'>
              <Text>
              Home
              </Text>
              </NavLink>
              <NavLink element='features' path='/features'>
              <Text>
              Features
              </Text>
              </NavLink>
              <NavLink element='pricing' path='/pricing'>
              <Text>
              Pricing
              </Text></NavLink>
            </HStack>
            <HStack
                spacing={8} 
                alignItems={'center'}
                width="100%"
                >
                <FormControl>
                        <Flex
                        flexDir="row"
                        width="100%"
                        >
                            <FormHelperText color="blue.200" fontSize="sm" mr={5}>Search</FormHelperText>
                            <Input
                            borderColor="blue.200"
                            bg="white"
                            h={25}
                            />
                        </Flex>
                </FormControl>
            </HStack>
            <Flex>
                <NavLink element='Login' path='/login'>Login</NavLink>
                <NavLink element='Sign Up' path='/signup'>Sign up</NavLink>
            </Flex>
          </HStack>
        </Flex>
      </Container>
  );
}

export default NavigationBar;
