import {
    Container,
    SimpleGrid,
    Divider,
    List,
    ListIcon,
    ListItem,
    Image,
    Flex,
    Heading,
    Text,
    Stack,
    StackDivider,
    Box,
    Icon,
    Avatar,
    useColorModeValue,
  } from '@chakra-ui/react';
  import {
    IoAnalyticsSharp,
    IoLogoBitcoin,
    IoSearchSharp,
  } from 'react-icons/io5';
  import { ReactElement } from 'react';
  

  export default function Description_1() {
    return (
      <Box py={6} px={5} min={'100vh'}>
        <Stack spacing={4} width={'100%'} direction={'column'}>
          <Stack
            p={5}
            alignItems={'center'}
            justifyContent={{
              base: 'flex-start',
              md: 'space-around',
            }}
            direction={{
              base: 'column',
              md: 'row',
            }}>
            <Stack
              width={{
                base: '100%',
                md: '40%',
              }}
              textAlign={'center'}>
              <Heading size={'lg'}>
                The Right Plan for <Text color="purple.400">Your Business</Text>
              </Heading>
            </Stack>
            <Stack
              width={{
                base: '100%',
                md: '60%',
              }}>
              <Text textAlign={'center'}>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Numquam
                quod in iure vero. Facilis magnam, sed officiis commodi labore
                odit.
              </Text>
            </Stack>
          </Stack>
          <Divider />
          <PackageTier title={'Starter'} typePlan="Free" options={options} />
          <Divider />
          <PackageTier
            title={'Lorem Plus'}
            checked={true}
            typePlan="$32.00"
            options={options}
          />
          <Divider />
          <PackageTier title={'Lorem Pro'} typePlan="$50.00" options={options} />
        </Stack>
      </Box>
    );
  }
