import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Heading,
  Text,
  Stack,
  Container,
  Avatar,
  useColorModeValue,
  Image
} from '@chakra-ui/react';



export default function Description_4() {
  return (
    <Box
    bg='url(images/bg_0.png)'
    backgroundRepeat="no-repeat"
    backgroundSize="cover"
    >
      <Container maxW={'7xl'} py={16} as={Stack} spacing={12}>
        <Stack
           display="flex"
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 6, lg: 10 }}
          justifyContent="space-between"
          >

            <Stack
            flex={1}
            spacing={0} 
            align={'center'}
            padding={10}
            >
                <Image
                h={500}
                w={'100%'}
                rounded={'md'}
                alt={'feature image'}
                src={
                  'images/finance.png'
                }
                objectFit={'fill'}
                />
            </Stack>

            <Stack 
           flex={2}
            spacing={0} 
            align={'center'}
            pt={0}
            padding={0}
            mt={50}>
            <Heading
            color='#175E7D'
            >
              Multiple Inventories
            </Heading>
              <Text
              fontSize={30}
              align={'center'}
              color='#175E7D'
              px={6}
              >
                It's easy to create multiple separate inventories whether it's for different departments,
                different items, or completely different uses. switching different inventories, only takes a click
              </Text>
            </Stack>
            
        </Stack>
      </Container>
    </Box>
  );
}
