import {
    Container,
    SimpleGrid,
    Divider,
    List,
    ListIcon,
    ListItem,
    Image,
    Flex,
    Heading,
    Text,
    Stack,
    StackDivider,
    Box,
    Icon,
    Avatar,
    Button,
    useColorModeValue,
  } from '@chakra-ui/react';
  import {
    IoAnalyticsSharp,
    IoLogoBitcoin,
    IoSearchSharp,
  } from 'react-icons/io5';
  import { ReactElement } from 'react';
  import { FaCheckCircle } from 'react-icons/fa';
  
  export default function Description_1() {
    return (
      <Container
      py={6}
      px={5} 
      maxW={'10xl'}
      bg='url(images/bg_1.png)'
      backgroundRepeat="no-repeat"
      backgroundSize="cover"
      align-alignItems={'center'}
      justifyContent='space-between'
      >
        <Stack 
        spacing={4}
        width={'100%'} 
        min={'100vw'}
        direction={'column'}
        >
          <Stack 
          spacing={40}
          width={'100%'} 
          direction={'row'}
          >
              <Flex
              mt={150}
              top="-10%"
              >
                <Image
                    rounded={'md'}
                    alt={'feature image'}
                    src={
                      'images/boxes_man.png'
                    }
                    objectFit={'cover'}
                    
                  />
              </Flex>
              <Stack
              bg="rgba(0,0,0,0.3)"
              paddingLeft={75}
              padding={120}
              color="white"
              fontFamily='Montserrat'
              w={550}
              >
                <Heading
                fontWeight={500}
                fontSize={40}
                mb={30}
                >
                  Helps you manage your inventory business easily
                </Heading>
                <Text
                fontSize={25}
                fontWeight={600}
                >
                Run efficient business with the best inventory management system
                </Text>
              </Stack>
        </Stack>
        </Stack>
      </Container>
    );
  }
