import {
    Container,
    SimpleGrid,
    Image,
    Flex,
    Heading,
    Text,
    Stack,
    StackDivider,
    Box,
    Icon,
    Avatar,
    useColorModeValue,
  } from '@chakra-ui/react';
  import {
    IoAnalyticsSharp,
    IoLogoBitcoin,
    IoSearchSharp,
  } from 'react-icons/io5';
  import { ReactElement } from 'react';
  

  export default function Description_1() {
    return (

      <Box bg={useColorModeValue('gray.100', 'gray.700')}>
      <Container maxW={'7xl'} py={16} as={Stack} spacing={12}>
        <Stack spacing={0} align={'center'}>
          <Heading>Our Clients Speak</Heading>
          <Text>We have been working with clients around the world</Text>
        </Stack>
        <Stack
        bg='url(images/bg_1.png)'
        spacing={0}
        align={'center'}
        >
       <Flex
       flexDir="row"
       >
          <Flex
          >
            <Image
                rounded={'md'}
                alt={'feature image'}
                src={
                  'images/boxes_man.png'
                }
                objectFit={'cover'}
              />
          </Flex>
            <Flex
            pt={100}
            w={600}
            h={600}
            ml={250}
            bg="rgba(0,0,0,0.3)"
            >
            <Box>
                <Box
                paddingTop={8}
                pl={50}
                pr={300}
                >
                  <Text
                  position="relative"
                  fontSize={40}
                  color={'white'}
                  fontFamily="Montserrat"
                  fontWeight={600}
                  >
                    Helps you manage your inventory and business easily
                  </Text>

                </Box>
                <Box
                pl={50}
                pt={100}
                >
                    <Text
                    fontSize={25}
                    color={'white'}
                    fontFamily="Montserrat"
                    fontWeight={400}
                    >
                      Run a more efficient business with the best inventory management system
                    </Text>
                </Box>
            </Box>
            </Flex>
        </Flex>
        
    </Stack>
      </Container>
    </Box>
    
      
    );
  }
