import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Heading,
  Text,
  Stack,
  Container,
  Avatar,
  useColorModeValue,
  Image
} from '@chakra-ui/react';



export default function Description_2() {
  return (
    <Box bg="white">
      <Container maxW={'7xl'} py={16} as={Stack} spacing={12}>
        <Stack 
        spacing={0} 
        align={'center'}
        mt={100}
        fontFamily="Montserrat"
        >
          <Heading
          color='#175E7D'
          >
          Wanna keep track of all your inventory ?
          </Heading>
        </Stack>
        <Stack
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 4, lg: 10 }}>

            <Stack 
            spacing={0} 
            align={'center'}
            padding={120}
            mt={100}>
              <Text
              fontSize={30}
              align={'center'}
              color='#175E7D'
              >
                Cloud based Inventory Management System that allows you to make changes accross multiple brands
              </Text>
            </Stack>
            <Stack
            spacing={0} align={'center'}
            >
            <Image
                    rounded={'md'}
                    alt={'feature image'}
                    src={
                      'images/teacher.png'
                    }
                    objectFit={'cover'}
                    
                  />
            </Stack>
        </Stack>
      </Container>
    </Box>
  );
}
