import { ReactNode } from 'react';
import {
  Box,
  Flex,
  Heading,
  Text,
  Stack,
  Container,
  Avatar,
  useColorModeValue,
  Image
} from '@chakra-ui/react';



export default function Description_3() {
  return (
    <Box  
    bg='url(images/bg_0.png)'
    backgroundRepeat="no-repeat"
    backgroundSize="cover"
    >
      <Container maxW={'80%'} py={16} as={Stack} spacing={12}>
        <Stack
          display="flex"
          direction={{ base: 'column', md: 'row' }}
          spacing={{ base: 10, md: 6, lg: 10 }}
          justifyContent="space-between"
          >

            <Stack 
            spacing={0} 
            align={'center'}
            pt={0}
            padding={70}
            flex={1.5}
            mt={35}>
            <Heading
            color='#175E7D'
            >
              Great Support
            </Heading>
              <Text
              fontSize={30}
              align={'center'}
              color='#175E7D'
              px={6}
              >
                We actively help clients think through their inventory management processes, 
                help them configure their inventory, and even organize and upload their data. And If
                you call or email about an issue, you will connect with a real person right away.
              </Text>
            </Stack>
            <Stack
            flex={1}
            spacing={0} 
            align={'center'}
            padding={20}
            >
            <Image
            w={1000}
            h={800}
            rounded={'md'}
            alt={'feature image'}
            src={
              'images/graph_paper.png'
            }
            objectFit={'fill'}
            />
            </Stack>
        </Stack>
      </Container>
    </Box>
  );
}
